function deleteProductCategory(n,t){Swal.fire({title:t,text:"Silmek istediğinize emin misiniz ?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Evet , Sil!",cancelButtonText:"İptal"}).then(t=>{t.value&&$.ajax({type:"POST",url:"/sinyal-sil",data:{id:n},success:function(n){n.ec==0?(Swal.fire("Silindi!","Sinyal silme işlemi başarılı.","success"),table.ajax.reload()):n.ec==-1?Swal.fire("Hata!","Sinyal silme sırasında hata oluştu..","warning"):Swal.fire("Hata!","Bir şeyler ters gitti..","warning")}})})}var table=$("#RestTable").DataTable({processing:!0,serverSide:!0,filter:!0,ajax:{url:"/sinyal-liste",type:"POST",datatype:"json"},columnDefs:[{targets:0,data:null,visible:!1},{targets:1,data:"buySell",className:"control",sortable:!1,autoWidth:!0},{targets:2,data:"entry",className:"control",sortable:!1,autoWidth:!0},{targets:3,data:"tp",className:"control",sortable:!1,autoWidth:!0},{targets:4,data:"sl",autoWidth:!0,sortable:!1},{targets:5,data:null,sortable:!1,autoWidth:!0,defaultContent:"",render:(n,t,i)=>[`   <button type="button" class="btn btn-sm bg-secondary text-white edit-productCategory" data-productCategory-id="${i.id}">`,`       <i class="fas fa-pencil-alt"></i> Düzenle`,"   <\/button>",`   <button type="button" class="btn btn-sm bg-danger text-white delete-productCategory" data-productCategory-id="${i.id}" data-productCategory-name="${i.buySell}">`,`       <i class="fas fa-trash"></i> Sil`,"   <\/button>"].join("")}],language:{url:"//cdn.datatables.net/plug-ins/9dcbecd42ad/i18n/Turkish.json"}});$(document).on("click",".edit-productCategory",function(){var n=$(this).attr("data-productCategory-id");window.location.href="/sinyal-duzenle/"+n});$(document).on("click",".delete-productCategory",function(){var n=$(this).attr("data-productCategory-id"),t=$(this).attr("data-productCategory-name");deleteProductCategory(n,t)});