function deleteuser(n,t){Swal.fire({title:t,text:"Silmek istediğinize emin misiniz ?",icon:"warning",showCancelButton:!0,confirmButtonColor:"#3085d6",cancelButtonColor:"#d33",confirmButtonText:"Evet , Sil!",cancelButtonText:"İptal"}).then(t=>{t.value&&$.ajax({type:"POST",url:"/kullanici-sil",data:{id:n},success:function(n){n?(Swal.fire("Silindi!","Kullanıcı silme işlemi başarılı.","success"),table.ajax.reload()):Swal.fire("Hata!","Kullanıcı silme sırasında hata oluştu..","warning")}})})}var table=$("#UserTable").DataTable({processing:!0,serverSide:!0,filter:!0,ajax:{url:"/kullanici-liste",type:"POST",datatype:"json"},columnDefs:[{targets:0,data:null,visible:!1},{targets:1,data:"name",className:"control",sortable:!1,autoWidth:!0},{targets:2,data:"surname",autoWidth:!0,sortable:!1},{targets:3,data:"email",autoWidth:!0,sortable:!1},{targets:4,data:"phone",autoWidth:!0,sortable:!1},{targets:5,data:"isActive",autoWidth:!0,sortable:!1,render:n=>`<input type="checkbox" disabled ${n?"checked":""}>`},{targets:6,data:null,sortable:!1,autoWidth:!1,autoWidth:!0,defaultContent:"",render:(n,t,i)=>[`   <button type="button" class="btn btn-sm bg-secondary text-white edit-user" data-user-id="${i.id}">`,`       <i class="fas fa-pencil-alt"></i> Düzenle`,"   <\/button>",`   <button type="button" class="btn btn-sm bg-danger text-white delete-user" data-user-id="${i.id}" data-user-name="${i.name}">`,`       <i class="fas fa-trash"></i> Sil`,"   <\/button>"].join("")}],language:{url:"//cdn.datatables.net/plug-ins/9dcbecd42ad/i18n/Turkish.json"}});$(document).on("click",".edit-user",function(){var n=$(this).attr("data-user-id");window.location.href="/kullanici-duzenle/"+n});$(document).on("click",".delete-user",function(){var n=$(this).attr("data-user-id"),t=$(this).attr("data-user-name");deleteuser(n,t)});